		@PK_sMalop	varchar(50)
		,@sTenlop	nvarchar(100)
		,@iNamvaotruong	smallint
		,@iSiso	smallint
		,@iSoNamDaoTao	smallint
		,@iTrangThai	smallint
AS
BEGIN
	SET NOCOUNT ON;
		
	UPDATE [tblLopHoc]	SET
		 [sTenlop] 	= @sTenlop
		,[iNamvaotruong] 	= @iNamvaotruong
		,[iSiso] 	= @iSiso
		,[iSoNamDaoTao] 	= @iSoNamDaoTao
		,[iTrangThai] 	= @iTrangThai
	WHERE 
		[PK_sMalop] 	= @PK_sMalop
		
END

